Scripts.OnUpdate:
self:SetAlpha(1.0);
if(WorldMapFrame.scale==WORLDMAP_RATIO_MINI) then
	local newScale=WorldMapFrame:GetEffectiveScale()*0.572;
	if(self:GetScale()~=newScale) then
		self:SetScale(newScale);
		self:ClearAllPoints();
		self:SetPoint("TOPLEFT", WorldMapPositioningGuide, "TOPLEFT", 28, -60);
		MobMap_PlaceMobMapButtonFrame();
	end
elseif(WorldMapFrame.scale==WORLDMAP_RATIO_SMALL) then
	local newScale=WorldMapFrame:GetEffectiveScale()*0.69;
	if(self:GetScale()~=newScale) then
		self:SetScale(newScale);
		self:ClearAllPoints();
		self:SetPoint("TOPLEFT", WorldMapPositioningGuide, "TOPLEFT", 10, -98);
		MobMap_PlaceMobMapButtonFrame();
	end
else
	local newScale=WorldMapFrame:GetEffectiveScale();
	if(self:GetScale()~=newScale) then
		self:SetScale(newScale);
		self:ClearAllPoints();
		self:SetPoint("TOPLEFT", WorldMapPositioningGuide, "TOPLEFT", 4, -67);
		MobMap_PlaceMobMapButtonFrame();
	end
end
if((not WorldMapFrame:IsVisible()) and self:IsVisible()) then
	self:Hide();
end
MobMap_ProcessDotEffects(self,arg1);

Scripts.OnEnter:
WorldMapTooltip:SetOwner(self, "ANCHOR_TOPRIGHT");
if(self.itemid~=nil) then
	MobMap_DisplayItemTooltip(WorldMapTooltip, self.itemid, self.ihid);
end

Scripts.OnClick:
                        if(self:GetChecked()) then
							PlaySound("igMainMenuOptionCheckBoxOn");
							mobmap_enabled=true;
							if(MobMap_Display) then
								MobMap_Display();
							end
                        else
							mobmap_enabled=false;
							if(MobMap_HideAllDots) then
								MobMap_HideAllDots();
							end
                        end

Scripts.OnUpdate:
if((not WorldMapFrame:IsVisible()) and self:IsVisible()) then
	self:Hide();
end

Scripts.OnLoad:
self.highlightR=1.0;
self.highlightG=1.0;
self.highlightB=1.0;
self.highlightA=1.0;
self.textR=1.0;
self.textG=0.82;
self.textB=0.0;
self.textA=1.0;
self.SetTextColor=function(self,r,g,b,a)
	self.textR=r;
	self.textG=g;
	self.textB=b;
	if(a) then self.textA=a;
	else self.textA=1.0; end
	self:GetFontString():SetTextColor(self.textR, self.textG, self.textB, self.textA);
end
self.SetHighlightTextColor=function(self,r,g,b,a)
	self.highlightR=r;
	self.highlightG=g;
	self.highlightB=b;
	if(a) then self.highlightA=a;
	else self.highlightA=1.0; end
end
self.SuperOnEnter=function(self)
	if(not self:IsEnabled()) then return; end
	self:GetFontString():SetTextColor(self.highlightR, self.highlightG, self.highlightB, self.highlightA);
end
self.SuperOnLeave=function(self)
	if(not self:IsEnabled()) then return; end
	self:GetFontString():SetTextColor(self.textR, self.textG, self.textB, self.textA);
end

Scripts.OnClick:
                        if(self:GetChecked()) then
							PlaySound("igMainMenuOptionCheckBoxOn");
							mobmap_minimap=true;
                        else
							mobmap_minimap=false;
							MobMap_HideAllMinimapDots();
                        end

                        if(self:GetChecked()) then
							PlaySound("igMainMenuOptionCheckBoxOn");
							mobmap_scanning=false;
                        else
							mobmap_scanning=true;
							MobMapFrame:RegisterEvent("PLAYER_TARGET_CHANGED");
                        end

