Scripts.OnClick:
if(arg1=="LeftButton") then
	MobMap_OpenRewardSelection(self);
end
if(arg1=="RightButton") then
	MobMap_ClearRewardSelection(self);
end

Scripts.OnEnter:
if(self:GetAlpha()>0 and self.itemstring~=nil) then
	MobMapTooltip:SetOwner(self, "ANCHOR_RIGHT", -100, 0);
	MobMap_DisplayItemTooltip(MobMapTooltip, self.itemid);
end

Scripts.OnClick:
if(not MobMapFrame:IsVisible()) then
	mobmap_questsearch=true;
end
MobMap_ShowPanel("MobMapMobSearchFrame");
MobMap_ShowMobByID(self.npcid);

if(not MobMapFrame:IsVisible()) then
	mobmap_questsearch=true;
end
MobMap_ShowPanel("MobMapMobSearchFrame");
MobMap_ShowMobByID(self.npcid);

if(self:GetChecked()) then
	PlaySound("igMainMenuOptionCheckBoxOn");
	MobMapQuestCommentEditorFrameIdentificationNo:SetChecked(false);
	mobmap_quest_comment_identification=true;
else
	MobMapQuestCommentEditorFrameIdentificationNo:SetChecked(true);
	mobmap_quest_comment_identification=false;
end

if(self:GetChecked()) then
	PlaySound("igMainMenuOptionCheckBoxOn");
	MobMapQuestCommentEditorFrameIdentificationYes:SetChecked(false);
	mobmap_quest_comment_identification=false;
else
	MobMapQuestCommentEditorFrameIdentificationYes:SetChecked(true);
	mobmap_quest_comment_identification=true;
end
